import { expect } from '@jest/globals';
import { test } from 'picomatch';
import {propFunc, propPath} from './prop-func';

it('prop path', () => {
  expect(propPath({a:1}, 'a')).toBe(1);
  expect(propPath({a: {b: {c: 1}}}, 'a.b.c')).toBe(1);
  expect(propPath({a: {b: {c: 1}}}, 'a.b.c.d')).toBe(undefined);
})

it('prop func', () => {
  expect(propFunc({a:1}, 'a')).toBe(1);
  expect(propFunc({a: {b: {c: 1}}}, 'a.b.c')).toBe(1);
  expect(propFunc({a: {b: {c: 1}}}, 'a.b.c.d')).toBe(undefined);

  expect(propFunc({a:1}, ({a}) => a)).toBe(1);
  expect(propFunc({a:1}, [])).toBe(undefined);
  expect(propFunc({a:1})).toBe(undefined);
})